import React from 'react';
import style from './StatusBar.module.css';
import heart from '../../assets/img/heart.png';
import emptyHeart from '../../assets/img/emptyHeart.png';

const StatusBar = ({ numberOfMistakes, percentage, CPM }) => {
    const [heartsCollection, handler] = React.useState([]);
    React.useE(() => {
        
        calculateHears()
    });
    const calculateHears = () => {
        setTimeout(handler([]), 0);
        for (let i = 0; i < 3 - numberOfMistakes; i++) {
            heartsCollection.push(<img key={i} src={heart} alt='heart icon' className={style.heart} />)
        }
        // for (let i = 0; i < numberOfMistakes - 1; i++) {
        //     heartsCollection.push(<img key={i} src={emptyHeart} alt='heart icon' className={style.heart} />)
        // }
    }
    

    return (
        <div className={style.statusBox}>
            <span className={style.heartsBox}>
                {heartsCollection}
            </span>

            <span className={style.percentageBox}> Progress: {percentage}%</span>
            <span className={style.cpmBox}> CPM: {CPM}</span>
        </div>
    )

}

export default StatusBar;
