import React from 'react';
import * as PropTypes from 'prop-types';
import FinishBoard from '../Board/FinishBoard';
import style from './CustomInput.module.css';
import ChangeLayout from '../common/ChangeLayout';
import DisplayText from '../Display/DisplayText';

const CustomInput = (props) => {
    const [isDisabled, disableInput] = React.useState(false);
    const [isPassed, passedResult] = React.useState(false);

    const [changeLayout, changeLayoutHandler] = React.useState(false);
    const [msPassed, msPassedHandler] = React.useState(0);
    const [value, changeValue] = React.useState('');

    React.useEffect(() => {
        console.log(value)
        if (value.length !== 0) {
            msPassedHandler(new Date().getTime());
            // handleMistake();
            completionPercentage();
            charactersPerMinute();

            props.passLastLetter(value.slice(-1));

        }
    }, [value]);

    React.useEffect(() => {
        if (value.length === props.generatedText.length) {
            passedResult(true);
            disableInput(true);
        }
    }, [value.length, props.generatedText.length]);

    React.useEffect(() => {
        if (props.numberOfMistakes === 2) {
            disableInput(true);
            passedResult(false);
        }

        if (value.slice(-1) !== props.generatedText[value.length - 1]) {
            props.mistakeCounter();
            props.handleMistake(true);
        } else {
            props.handleMistake(false);
        }

    });

    // const handleMistake = () => {
    //     if (props.numberOfMistakes === 2) {
    //         disableInput(true);
    //         passedResult(false);
    //     }

    //     if (value.slice(-1) !== props.generatedText[value.length - 1]) {
    //         props.mistakeCounter();
    //         props.handleMistake(true);
    //     } else {
    //         props.handleMistake(false);
    //     }
    // };

    const completionPercentage = () => {
        let percentage = Math.ceil(value.length / props.generatedText.length * 100);
        props.completionPercentage(percentage);
    };

    const charactersPerMinute = () => {
        let timePassedMs = new Date().getTime();
        let timePassedMin = (timePassedMs - msPassed) / 1000 / 60;
        let CPM = Math.ceil(value.length / timePassedMin);

        props.charactersPerMinute(CPM);
    };

    const reloadLesson = () => {
        msPassedHandler(0);
        disableInput(false);
        passedResult(false);
        changeValue('');

        props.reloadLesson();
    }

    return (
        <div className={style.InputBox}>
            <input autoFocus
                disabled={isDisabled}
                onChange={event => changeValue(event.target.value)}
                value={value}
            />

            <DisplayText generatedText={props.generatedText} value={value} />

            {isDisabled && <FinishBoard isPassed={isPassed} reloadLesson={reloadLesson} />}

            {changeLayout && <ChangeLayout layoutHandler={changeLayoutHandler} />}
        </div>
    )
};

export default CustomInput;
