import React from 'react';
import style from './Main.module.css';

import CustomInput from '../CustomInput/CustomInput';
import Keyboard from '../Keyboard/Keyboard';
import StatusBar from '../StatusBar/StatusBar';

const Main = (props) => {
  const [numberOfMistakes, mistakeCounter] = React.useState(0);
  const [percentage, completionPercentage] = React.useState(0);
  const [CPM, charactersPerMinute] = React.useState(0);
  const [mistakeHappened, handleMistake] = React.useState(false);
  const [lastLetter, passLastLetter] = React.useState('');

  const mistakesHandler = () => {
    mistakeCounter(numberOfMistake + 1);
  }

  const reloadLesson = () => {
    mistakeCounter(0);
    completionPercentage(0);
    charactersPerMinute(0);
    handleMistake(false);
    passLastLetter('');
    props.reloadLesson();
  }

  return (
    <main className={style.main}>

      <StatusBar numberOfMistake={numberOfMistake} mistakeHappened={mistakeHappened}
        percentage={percentage} CPM={CPM} />

      <CustomInput
        handleMistake={handleMistake}
        mistakeCounter={mistakesHandler}
        numberOfMistake={numberOfMistake}

        generatedText={props.generatedText}
        reloadLesson={reloadLesson}

        completionPercentage={completionPercentage}
        passLastLetter={passLastLetter}
        charactersPerMinute={charactersPerMinute}
      />
      <Keyboard mistakeHappened={mistakeHappened}
        lastLetter={lastLetter} />
    </main>)
}

export default Main;
