import React from "react";
import style from './Display.module.css';

const DisplayText = (props) => {

    const [generatedText, handler] = React.useState(props.generatedText);

    React.useEffect(() => {
        handler(generatedText.slice(props.value.length));
        console.log('update')
    })

    return (
        <span className={style.displayText}>
            <span></span>
            <span className={style.cursor} />
            <span className={style.unfinishedText}>{props.generatedText}</span>
        </span>
    )

}

export default DisplayText;
