import React from "react";
import style from './Display.module.css';

const DisplayText = (props) => {

    const [leftText, leftTextHandler] = React.useState('');
    const [finishText, finishTextHandler] = React.useState('');

    React.useEffect(() => {
        // let leftText = document.getElementById('leftText');
        // let finishText = document.getElementById('finishText');

        // finishText.textContent += leftText.textContent[0];
        // leftText.textContent = leftText.textContent.slice(1);

        finishTextHandler(finishText += leftText[0]);
        leftTextHandler(leftText += leftText.slice(1));
    })


    return (
        <span className={style.displayText}>
            <span id='finishText' className={style.cursor} value={finishText} />
            <span id='leftText' className={style.unfinishedText} value={leftText}>{props.generatedText}</span>
        </span>
    )

}

export default DisplayText;
