    let test =
        React.useEffect(() => {
            sss()
            console.log('update')
        }, [props.value])

    return (
        <span className={style.displayText}>
            <span>{props.value}</span>
            <span className={style.cursor} />
            <span className={style.unfinishedText}>{test}</span>
        </span>
    )

}

